# TCN personal info for LCC and CHC
welcome:DialogueUser
    welcome:hasAge		"{{tcnAge}}"^^xsd:integer ;
    welcome:hasGender		"{{tcnGender}}"^^xsd:string ;
    welcome:hasFamily		"{{tcnFamily}}"^^xsd:string ;
    welcome:hasNationality		"{{tcnNation}}"^^xsd:string ;
    welcome:hasReligion		"{{tcnReligion}}"^^xsd:string ;
    welcome:hasEthnicity		"{{tcnEthnic}}"^^xsd:string ;
    welcome:hasLccCpl "{{tcnLccCpl}}"^^xsd:double;
    welcome:hasAttendedLessonIDs "1"^^xsd:integer, "2"^^xsd:integer .

# TCN preferences info for LCC and CHC
welcome:DialogueUser 
    welcome:hasChcAgePreference	_:age1_1 ;
    welcome:hasChcGenderPreference		"{{chcGenderPref}}"^^xsd:string ;
    welcome:hasChcFamilyPreference		'Single Woman', 'Single Parent Mother' ;
    welcome:hasChcNationPreference		"{{chcNationPref}}"^^xsd:string ;
    welcome:hasChcReligionPreference		"{{chcReligionPref}}"^^xsd:string ;
    welcome:hasChcEthnicPreference		"{{chcEthnicPref}}"^^xsd:string ;
    welcome:hasChcLocationPreference		'Dont mind' ;
    welcome:hasChcAccessibilityPreference	^^xsd:string ;
    welcome:hasChcRentPeriodPreference	_:rent1_1 ;
    welcome:hasChcShareWithPreference	_:share1_1 ;
    welcome:hasLccGenderPreference "Dont mind"^^xsd:string ;
    welcome:hasLccNationPreference "Same"^^xsd:string ;
    welcome:hasLccGenderPrefWeight "0.75"^^xsd:float ;
    welcome:hasLccNationPrefWeight "0.25"^^xsd:float .

_:age1_1    welcome:lowerBound     '18';welcome:higherBound	'25'.
_:rent1_1  welcome:startDate	'2021-01-01'^^xsd:date;     welcome:endDate	'2021-07-01'^^xsd:date.
_:share1_1  welcome:min '2'^^xsd:integer;   welcome:max '3'^^xsd:integer.


###########    MAJAN Related Initial Knowledge - END #################################
################################################################################

:initialization ajan:agentInitKnowledge welcome:KMS, welcome:dispatcher .

welcome:KMS welcome:hasURI "http://localhost:8083/kms/api/dips/input" .
