@prefix : <http://localhost:8090/rdf4j/repositories/welcome_ontology#> .
@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix xml: <http://www.w3.org/XML/1998/namespace> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .
@prefix ajan: <http://www.ajan.de/ajan-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix welcome: <https://raw.githubusercontent.com/gtzionis/WelcomeOntology/main/welcome.ttl#> .

#-------------------------------Message Type---------------------------------
welcome:Message     welcome:hasType     welcome:StartLccAssessment .

welcome:LccAssessmentPhase  welcome:hasLessonId     '1'^^xsd:integer ;
                            welcome:hasDeadline     '2021-05-01T15:00'^^xsd:dateTime .
#----------------------------------------------------------------------------

#-------------------------------Message Type---------------------------------
welcome:Message     welcome:hasType     welcome:StartLccScenario .

_:bnode_lccUseCase  rdf:type welcome:LCCUseCase ;
                    welcome:hasParticipants 
                    "majanAgent1", "majanAgent2", "majanAgent3", "majanAgent4", "majanAgent5" ;
                    welcome:hasLessonId     "3" ;
                    welcome :hasDedicatedAgent   "majanAgent1" ;
                    welcome:hasSdWeight    "0.75" ;
                    welcome:hasId       "1234" .
#----------------------------------------------------------------------------

#-------------------------------Message Type---------------------------------
welcome:Message     welcome:hasType     welcome:InformLccFinalResult .

?LccUseCaseSubjectIriReceivedFromAgents     rdf:type    welcome:LCCUseCase ;
                                            welcome:hasId   ?lccId ;
                                            welcome:hasFinalSolution    ?lccSolution .
?lccSolution    rdf:type    welcome:Grouping ;
                welcome:hasMembers  ?memberGroup .
?memberGroup     rdf:type   welcome:Group ;
                welcome:hasMembers  ?memberAgentId .

#----------------------------------------------------------------------------

@prefix : <http://localhost:8090/rdf4j/repositories/welcome_ontology#> .
@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix xml: <http://www.w3.org/XML/1998/namespace> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .
@prefix ajan: <http://www.ajan.de/ajan-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix welcome: <https://raw.githubusercontent.com/gtzionis/WelcomeOntology/main/welcome.ttl#> .


#-------------------------------Message Type---------------------------------
welcome:Message welcome:hasType welcome:SubmitAssessmentScore .
_:bnode_lcclesson   rdf:type    welcome:LCCLesson ;
                    welcome:hasLessondId    ?lessonId ;
                    welcome:hasAssessmentScore  ?assessmentScore ;
                    welcome:hasReadingScore     ?readingScore ;
                    welcome:hasWritingScore     ?writingScore ;
                    welcome:hasListeningScore   ?listeningScore ;
                    welcome:hasVocabularyScore  ?vocabularyScore .
#----------------------------------------------------------------------------

#-------------------------------Message Type---------------------------------
welcome:Message welcome:hasType welcome:StartChcScenario .

